function clipDuplicates(str){return nativ.replace.call(str,/([\s\S])(?=[\s\S]*\1)/g,'');}
function copyRegex(regex,options){if(!XRegExp.isRegExp(regex)){throw new TypeError('Type RegExp expected');}
